package _03_numbers;

public class _02_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int classes=65;
		int absentees= 10;
		//operators are the same as those from java script, which makes sense they are the common symbols for operators
		System.out.println(classes-absentees); //just imagine var is expanded to have different fields of numbers
		System.out.println(classes+absentees);
		System.out.println(classes*absentees);
		System.out.println(classes/absentees);
		System.out.println(classes%absentees);
		//Remember equaliy is == and !=
	}

}
